css.dashboard:
#tile-[tile_id] {
    height: 100%;
}

#tile-[tile_id] .tile-primary {
    line-height: 1.2em;
}

#tile-[tile_id] .tile-primary:before {
    display: block;
    line-height: 1.2em;
    font-size: 40px;
    visibility: visible;
}

#tile-[tile_id] .tile-primary:after {
    font-size: 18px;
    margin-left: 0px;
    line-height: 1.2em;
     display: none; 
}

bpt-currentDataTile.css:
#tile-[tile_id] .tile-title {
    position: absolute;
    bottom: .5em;
    left: .5em;
}

#tile-[tile_id] .tile-primary tbody:nth-child(1) td small {
    position: absolute;
    right: 3.5em;
}

#tile-[tile_id] .tile-primary {
    line-height: 1.4em;
}

bpt-history.css:
#tile-[tile_id] {
  background-color: rgba(0,0,0,.6);
}

#tile-[tile_id] table {
  border-collapse: collapse;
}

#tile-[tile_id] .tile-contents {
  padding: 0;
}

#tile-[tile_id] .tile-title {
position: absolute;
  bottom: .75em;
  right: 1.25em;
  width: auto;
  text-align: right;
  opacity: .5;
}

#tile-[tile_id] tr {
    display: flex;
}

#tile-[tile_id] .tile-primary tbody tr:nth-child(1) td p:nth-child(2) {
  position: absolute;
  top: 0;
  right: 0;
  padding: .5em 0 .25em .5em;
  background-color: rgba(255,255,255,.7);
  border-radius: 0 0 0 15px;
  color: rgba(0,0,0,0.7);
  width: unset !important;
  padding-top: 3em;
  font-size: 14px !important;
  margin-right: -1em;
}

#tile-[tile_id] .tile-primary tbody tr:nth-child(1) td p:nth-child(1) {
  text-align: left;
}

#tile-[tile_id] .tile-primary tbody tr:nth-child(1) td:nth-child(1) {
  text-align: left;
  width: 135px !important;
}

#tile-[tile_id] .tile-primary tbody tr:nth-child(1) td:nth-child(2) {
  vertical-align: top;
  padding: .25em .5em;
  text-align: left;
  vertical-align: top;
}

#tile-[tile_id] .tile-primary {
  line-height: 1.4em;
  padding: 0;
  vertical-align: top;
}

#tile-[tile_id] .tile-primary>div {
  overflow: hidden !important;
  height: unset !important;
  margin: 0;
}

#tile-[tile_id] .tile-primary img {
  text-align: left;
  margin-top: -1px;
  margin-left: -1px;
}

#tile-[tile_id] .tile-primary tbody tr:nth-child(1) td p:nth-child(2):after {
  content: " ";
  background-image: url(https://hubitat.ezeek.us/smartly-base/assets/images/logo_life360_75px.png);
  width: 100px;
  height: 3em;
  display: block;
  position: absolute;
  top: 0;
  margin-top: .5em;
  background-repeat: no-repeat;
  background-position: .25em 0;
}

